import { Component, Input } from '@angular/core';
import { Jogo } from 'src/app/models/jogo';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent {
  @Input() jogo!: Jogo;
}
<div class="container d-flex justify-content-center">
  <div class="row">
    <div class="card" style="width: 18rem;">
      <img src="{{ jogo.imagem }}" class="card-img-top" alt="...">
      <div class="card-body">
        <h5 class="card-title">{{ jogo.nome }}</h5>
        <p class="card-text">{{ jogo.descricao }}</p>
        <span class="badge badge-pill badge-success p-1 mb-2 justify-content-center">R$:{{ jogo.preco }}</span>
        <div class="container d-flex justify-content-center">
          <a href="{{ jogo.site }}" class="btn btn-primary">Página do Jogo</a>
        </div>
      </div>
    </div>
  </div>
</div>
