import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Jogo } from 'src/app/models/jogo';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent {

  jogos!: Jogo[];

  constructor(private HttpClient: HttpClient){}

  ngOnInit(): void {
    this.getJogos();
  }

  getJogos(){
    this.httpClient.get<Jogo[]>("http://localhost:8080/jogos").subscribe(retorno => {
      this.jogos = retorno;
    })
  }

  //save(){
  //  this.httpClient.post<Jogo>("http://localhost:8080/jogos").subscribe(retorno => {
  //    this.jogos = retorno;
  //  })
  //}
}
<div class="container d-flex justify-content-center">
  <div class="row" *ngFor="let jogo of jogos">
    <div class="card" style="width: 18rem;">
      <img src="{{ jogo.imagem }}" class="card-img-top" alt="...">
      <div class="card-body">
        <h5 class="card-title">{{ jogo.nome }}</h5>
        <p class="card-text">{{ jogo.descricao }}</p>
        <span class="badge badge-pill badge-success p-1 mb-2 justify-content-center">R$:{{ jogo.preco }}</span>
        <div class="container d-flex justify-content-center">
          <a href="{{ jogo.site }}" class="btn btn-primary">Página do Jogo</a>
        </div>
      </div>
    </div>
  </div>
</div>
